Test some things with the dojo library.
<script type="text/javascript" src="/static/dojo/dojo.js" djconfig="parseOnLoad: true">&amp;nbsp;</script>
<script type="text/javascript">dojo.require("dijit.form.Button");</script>
<script>function helloCallback(data,ioArgs) {
         alert(data);
      }       
      function helloError(data, ioArgs) {
         alert('Error when retrieving data from the server!');
      }</script>
<button dojotype="dijit.form.Button" id="helloButton">
 Hello World!
 <script type="dojo/method" event="onClick">dojo.xhrGet({
       url: 'request.txt',
       load: helloCallback,
       error: helloError
  });</script>
</button>
