hopobject.body:
 Test some things with the dojo library.
 <script type="text/javascript" src="/static/dojo/dojo.js" djconfig="parseOnLoad: true">&amp;nbsp;</script>
 <script type="text/javascript">dojo.require("dijit.form.Button");
dojo.require("dojox.image.SlideShow");
dojo.require("dojo.data.ItemFileReadStore");

function helloCallback(data,ioArgs) {
          alert(data);
       }       
       function helloError(data, ioArgs) {
          alert('Error when retrieving data from the server!');
       }</script>
 <button dojotype="dijit.form.Button" id="helloButton">
  Hello World!
  <script type="dojo/method" event="onClick">dojo.xhrGet({
        url: 'request.txt',
        load: helloCallback,
        error: helloError
   });</script>
 </button>
 <div dojotype="dojox.image.SlideShow" id="slideshow1" slideshowinterval="3" imagewidth="600" imageheight="300" fixedheight="true">titleTemplate="My title is '${title}', this is image ${current} out of ${total}"</div>
 <script type="text/javascript">dojo.addOnLoad(function() {
var imageItems = { 
   items: [
 {
   "large":"/static/banners/asp_dsp_bb_125x125.png",
   "title":"125x125 PNG",
   "link":"http://beagleboard.org"
 },
 {
   "large":"/static/banners/asp_dsp_bb_125x125.gif",
   "title":"125x125 GIF",
   "link":"http://beagleboard.org"
 },
 {
   "large":"/static/banners/asp_dsp_bb_336x280.png",
   "title":"336x280 PNG",
   "link":"http://beagleboard.org"
 },
 {
   "large":"/static/banners/asp_dsp_bb_728x90.png",
   "title":"728x90 PNG",
   "link":"http://beagleboard.org"
 }
]};
  var imageItemStore = new dojo.data.ItemFileReadStore({data: imageItems});

  //Define the request, saying that 5 records should be fetched at a time, 
  //and to start at record 0
  var request= {count:5, start:0};

  //Tell the widget to request the "large" parameter, as different 
  //stores may use different parameter names
  var itemNameMap = {imageLargeAttr: "large"};

  //Call the setDataStore function, passing it the data store, the request object, 
  //and the name map.
  dijit.byId('slideshow1').setDataStore(imageItemStore, request, itemNameMap);
});</script>
